import React, { useState, useEffect } from 'react';
import { api } from './api';

interface User {
  id: number;
  username: string;
}

interface Comment {
  id: number;
  username: string;
  operation: string;
  operand: number;
  result: number;
  createdAt: string;
  children: Comment[];
}

interface Discussion {
  id: number;
  username: string;
  startNumber: number;
  createdAt: string;
  comments: Comment[];
}

const App: React.FC = () => {
  const [user, setUser] = useState<User | null>(null);
  const [token, setToken] = useState<string | null>(localStorage.getItem('token'));
  const [discussions, setDiscussions] = useState<Discussion[]>([]);
  const [authMode, setAuthMode] = useState<'login' | 'register'>('login');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  useEffect(() => {
    if (token) {
      try {
        const payload = JSON.parse(atob(token.split('.')[1]));
        setUser({ id: payload.id, username: payload.username });
      } catch (err) {
        console.error('Invalid token:', err);
        localStorage.removeItem('token');
        setToken(null);
      }
    }
    loadDiscussions();
  }, [token]);

  const loadDiscussions = async () => {
    try {
      const data = await api.getDiscussions();
      setDiscussions(data);
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Failed to load discussions';
      setError(errorMessage);
    }
  };

  const handleAuth = async (e: React.FormEvent) => {
    e.preventDefault();
    setError('');
    try {
      const data = authMode === 'login' 
        ? await api.login(username, password)
        : await api.register(username, password);
      
      setToken(data.token);
      setUser(data.user);
      localStorage.setItem('token', data.token);
      setUsername('');
      setPassword('');
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Authentication failed';
      setError(errorMessage);
    }
  };

  const handleLogout = () => {
    setToken(null);
    setUser(null);
    localStorage.removeItem('token');
  };

  return (
    <div style={{ maxWidth: '1200px', margin: '0 auto', padding: '20px', fontFamily: 'sans-serif' }}>
      <h1>Number Discussion System</h1>
      
      <AuthSection 
        user={user}
        authMode={authMode}
        setAuthMode={setAuthMode}
        username={username}
        setUsername={setUsername}
        password={password}
        setPassword={setPassword}
        error={error}
        handleAuth={handleAuth}
        handleLogout={handleLogout}
      />

      {user && token && (
        <NewDiscussionForm 
          token={token} 
          onDiscussionCreated={loadDiscussions} 
        />
      )}

      <DiscussionList 
        discussions={discussions}
        user={user}
        token={token}
        onCommentAdded={loadDiscussions}
      />
    </div>
  );
};

interface AuthSectionProps {
  user: User | null;
  authMode: 'login' | 'register';
  setAuthMode: (mode: 'login' | 'register') => void;
  username: string;
  setUsername: (username: string) => void;
  password: string;
  setPassword: (password: string) => void;
  error: string;
  handleAuth: (e: React.FormEvent) => void;
  handleLogout: () => void;
}

const AuthSection: React.FC<AuthSectionProps> = ({ 
  user, authMode, setAuthMode, username, setUsername, 
  password, setPassword, error, handleAuth, handleLogout 
}) => {
  if (user) {
    return (
      <div style={{ marginBottom: '20px', padding: '15px', background: '#f0f0f0', borderRadius: '5px' }}>
        <span>Logged in as: <strong>{user.username}</strong></span>
        <button onClick={handleLogout} style={{ marginLeft: '10px' }}>Logout</button>
      </div>
    );
  }

  return (
    <div style={{ marginBottom: '20px', padding: '15px', background: '#f0f0f0', borderRadius: '5px' }}>
      <form onSubmit={handleAuth}>
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          style={{ marginRight: '10px', padding: '5px' }}
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          style={{ marginRight: '10px', padding: '5px' }}
        />
        <button type="submit">{authMode === 'login' ? 'Login' : 'Register'}</button>
        <button 
          type="button" 
          onClick={() => setAuthMode(authMode === 'login' ? 'register' : 'login')}
          style={{ marginLeft: '10px' }}
        >
          {authMode === 'login' ? 'Need an account?' : 'Have an account?'}
        </button>
      </form>
      {error && <div style={{ color: 'red', marginTop: '10px' }}>{error}</div>}
    </div>
  );
};

interface NewDiscussionFormProps {
  token: string;
  onDiscussionCreated: () => void;
}

const NewDiscussionForm: React.FC<NewDiscussionFormProps> = ({ 
  token, onDiscussionCreated 
}) => {
  const [startNumber, setStartNumber] = useState('');

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      await api.createDiscussion(parseFloat(startNumber), token);
      setStartNumber('');
      onDiscussionCreated();
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Failed to create discussion';
      alert(errorMessage);
    }
  };

  return (
    <div style={{ marginBottom: '20px', padding: '15px', background: '#e8f4f8', borderRadius: '5px' }}>
      <h3>Start New Discussion</h3>
      <form onSubmit={handleSubmit}>
        <input
          type="number"
          step="any"
          placeholder="Starting number"
          value={startNumber}
          onChange={(e) => setStartNumber(e.target.value)}
          style={{ marginRight: '10px', padding: '5px' }}
          required
        />
        <button type="submit">Start Discussion</button>
      </form>
    </div>
  );
};

interface DiscussionListProps {
  discussions: Discussion[];
  user: User | null;
  token: string | null;
  onCommentAdded: () => void;
}

const DiscussionList: React.FC<DiscussionListProps> = ({ 
  discussions, user, token, onCommentAdded 
}) => {
  return (
    <div>
      <h2>All Discussions</h2>
      {discussions.map((discussion) => (
        <DiscussionItem 
          key={discussion.id} 
          discussion={discussion}
          user={user}
          token={token}
          onCommentAdded={onCommentAdded}
        />
      ))}
    </div>
  );
};

interface DiscussionItemProps {
  discussion: Discussion;
  user: User | null;
  token: string | null;
  onCommentAdded: () => void;
}

const DiscussionItem: React.FC<DiscussionItemProps> = ({ 
  discussion, user, token, onCommentAdded 
}) => {
  return (
    <div style={{ 
      marginBottom: '30px', 
      padding: '15px', 
      border: '2px solid #333', 
      borderRadius: '5px' 
    }}>
      <div style={{ marginBottom: '10px' }}>
        <strong>{discussion.username}</strong> started with: 
        <span style={{ 
          fontSize: '24px', 
          fontWeight: 'bold', 
          marginLeft: '10px',
          color: '#0066cc'
        }}>
          {discussion.startNumber}
        </span>
        <span style={{ fontSize: '12px', color: '#666', marginLeft: '10px' }}>
          {new Date(discussion.createdAt).toLocaleString()}
        </span>
      </div>
      
      {user && token && (
        <CommentForm 
          discussionId={discussion.id}
          parentId={null}
          previousNumber={discussion.startNumber}
          token={token}
          onCommentAdded={onCommentAdded}
        />
      )}

      {discussion.comments.map((comment) => (
        <CommentTree 
          key={comment.id}
          comment={comment}
          discussionId={discussion.id}
          user={user}
          token={token}
          onCommentAdded={onCommentAdded}
          level={0}
        />
      ))}
    </div>
  );
};

interface CommentTreeProps {
  comment: Comment;
  discussionId: number;
  user: User | null;
  token: string | null;
  onCommentAdded: () => void;
  level: number;
}

const CommentTree: React.FC<CommentTreeProps> = ({ 
  comment, discussionId, user, token, onCommentAdded, level 
}) => {
  const operationSymbol: Record<string, string> = {
    ADD: '+',
    SUBTRACT: '-',
    MULTIPLY: '×',
    DIVIDE: '÷'
  };

  return (
    <div style={{ marginLeft: `${level * 30}px`, marginTop: '10px' }}>
      <div style={{ 
        padding: '10px', 
        background: '#f9f9f9', 
        borderLeft: '3px solid #0066cc',
        marginBottom: '5px'
      }}>
        <div>
          <strong>{comment.username}</strong>: {operationSymbol[comment.operation] || comment.operation} {comment.operand} = 
          <span style={{ 
            fontSize: '20px', 
            fontWeight: 'bold', 
            marginLeft: '5px',
            color: '#0066cc'
          }}>
            {comment.result}
          </span>
        </div>
        <div style={{ fontSize: '12px', color: '#666' }}>
          {new Date(comment.createdAt).toLocaleString()}
        </div>
      </div>

      {user && token && (
        <CommentForm 
          discussionId={discussionId}
          parentId={comment.id}
          previousNumber={comment.result}
          token={token}
          onCommentAdded={onCommentAdded}
        />
      )}

      {comment.children.map((child) => (
        <CommentTree 
          key={child.id}
          comment={child}
          discussionId={discussionId}
          user={user}
          token={token}
          onCommentAdded={onCommentAdded}
          level={level + 1}
        />
      ))}
    </div>
  );
};

interface CommentFormProps {
  discussionId: number;
  parentId: number | null;
  previousNumber: number;
  token: string;
  onCommentAdded: () => void;
}

const CommentForm: React.FC<CommentFormProps> = ({ 
  discussionId, parentId, previousNumber, token, onCommentAdded 
}) => {
  const [operation, setOperation] = useState('ADD');
  const [operand, setOperand] = useState('');
  const [showForm, setShowForm] = useState(false);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      await api.createComment(discussionId, parentId, operation, parseFloat(operand), token);
      setOperand('');
      setShowForm(false);
      onCommentAdded();
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Failed to create comment';
      alert(errorMessage);
    }
  };

  if (!showForm) {
    return (
      <button 
        onClick={() => setShowForm(true)}
        style={{ 
          marginTop: '5px', 
          padding: '5px 10px', 
          fontSize: '12px',
          background: '#0066cc',
          color: 'white',
          border: 'none',
          borderRadius: '3px',
          cursor: 'pointer'
        }}
      >
        Add operation
      </button>
    );
  }

  return (
    <form onSubmit={handleSubmit} style={{ marginTop: '5px', marginBottom: '10px' }}>
      <span style={{ marginRight: '5px' }}>{previousNumber}</span>
      <select 
        value={operation} 
        onChange={(e) => setOperation(e.target.value)}
        style={{ marginRight: '5px', padding: '3px' }}
      >
        <option value="ADD">+</option>
        <option value="SUBTRACT">-</option>
        <option value="MULTIPLY">×</option>
        <option value="DIVIDE">÷</option>
      </select>
      <input
        type="number"
        step="any"
        placeholder="number"
        value={operand}
        onChange={(e) => setOperand(e.target.value)}
        style={{ marginRight: '5px', padding: '3px', width: '80px' }}
        required
      />
      <button type="submit" style={{ marginRight: '5px', padding: '3px 8px' }}>Add</button>
      <button 
        type="button" 
        onClick={() => setShowForm(false)}
        style={{ padding: '3px 8px' }}
      >
        Cancel
      </button>
    </form>
  );
};

export default App;
